mapper.select:
SELECT * FROM agile_quick_filter WHERE project_id = #{projectId}
and (name LIKE CONCAT(CONCAT('%', #{filterName}), '%'))
AND (name LIKE CONCAT(CONCAT('%', #{content}), '%') OR express_query LIKE CONCAT(CONCAT('%', #{content}), '%') OR description LIKE CONCAT(CONCAT('%', #{content}), '%') )
