mapper.delete:
DELETE FROM fd_status_machine_transform
WHERE
organization_id = #{organizationId}
AND state_machine_id = #{stateMachineId}
AND (
   start_node_id = #{nodeId} OR end_node_id = #{nodeId}
)

mapper.select:
select fsmt.* from fd_status_machine_transform fsmt,
fd_status_machine_node fsmns,
fd_status_machine_node fsmne where fsmt.organization_id = #{organizationId} AND fsmt.state_machine_id = #{stateMachineId}
AND fsmns.id = fsmt.start_node_id AND fsmns.state_machine_id = fsmt.state_machine_id AND fsmns.status_id = #{statusId}
AND fsmt.type = 'transform_all'
AND fsmne.id = fsmt.end_node_id AND fsmne.state_machine_id = fsmt.state_machine_id AND fsmne.status_id = #{changeStatus}

SELECT *
FROM fd_status_machine_transform
WHERE organization_id = #{organizationId}
and state_machine_id = #{stateMachineId}
and (
  start_node_id = #{startNodeId}
  OR
  type = #{transformType}
)
